import React, { useEffect } from 'react';
import '../estilos/chat.css';

const Chat = () => {
  useEffect(() => {
    const script = document.createElement('script');
    script.type = 'module';
    script.src = 'https://cdn.jsdelivr.net/npm/@typebot.io/js@0.2/dist/web.js';
    script.onload = () => {
      // Initialize Typebot once the script is loaded
      if (window.Typebot) {
        window.Typebot.initPopup({ typebot: "whatsapp-ltd-estacio-ia" });
      }
    };
    document.body.appendChild(script);

    // Clean up script when component is unmounted
    return () => {
      document.body.removeChild(script);
    };
  }, []);

  return (
    <div className="chat-container">
      <h2 className="chat-title">Chat CR</h2>
      <div className="chat-box">
        <div className="messages">
          <div className="message bot-message">Olá! Como posso ajudar você hoje?</div>
          {/* Adicione aqui as mensagens do chat */}
        </div>
        <div className="input-container">
          <input type="text" placeholder="Digite sua mensagem..." className="chat-input" />
          <button className="send-button">Enviar</button>
        </div>
      </div>
    </div>
  );
}

export default Chat;
